import{_ as t,c as a,o as i,ae as r}from"./chunks/framework.B_owRgTZ.js";const d=JSON.parse('{"title":"Universal Auth Kit","description":"","frontmatter":{},"headers":[],"relativePath":"concept/VISION.md","filePath":"concept/VISION.md"}'),s={name:"concept/VISION.md"};function n(o,e,l,c,h,u){return i(),a("div",null,[...e[0]||(e[0]=[r('<h1 id="universal-auth-kit" tabindex="-1">Universal Auth Kit <a class="header-anchor" href="#universal-auth-kit" aria-label="Permalink to &quot;Universal Auth Kit&quot;">​</a></h1><h2 id="vision-document" tabindex="-1">Vision Document <a class="header-anchor" href="#vision-document" aria-label="Permalink to &quot;Vision Document&quot;">​</a></h2><h3 id="purpose" tabindex="-1">Purpose <a class="header-anchor" href="#purpose" aria-label="Permalink to &quot;Purpose&quot;">​</a></h3><p>Create a <strong>transport-agnostic, storage-agnostic, pluggable authentication library</strong> that:</p><ul><li>Supports multiple authentication strategies (JWT, OAuth, email/password, etc).</li><li>Does not store user data or depend on any specific transport (HTTP, CLI, gRPC, etc).</li><li>Provides a clean, composable, and type-safe interface for integrating authentication flows.</li><li>Enables users to plug in custom strategies easily without changing the core.</li></ul><h3 id="core-principles" tabindex="-1">Core Principles <a class="header-anchor" href="#core-principles" aria-label="Permalink to &quot;Core Principles&quot;">​</a></h3><ul><li><strong>Capability-driven:</strong> Strategies implement only the interfaces they need.</li><li><strong>Type-safe:</strong> Kernel returns fully typed strategy instances, eliminating runtime checks.</li><li><strong>Lightweight kernel:</strong> Acts as a registry and delegator, no heavy orchestration.</li><li><strong>Extensible:</strong> New strategies can be added without kernel modifications.</li><li><strong>User-centric:</strong> The host application manages transport and storage concerns.</li></ul><h3 id="target-users" tabindex="-1">Target Users <a class="header-anchor" href="#target-users" aria-label="Permalink to &quot;Target Users&quot;">​</a></h3><ul><li>Developers building apps that require flexible authentication options.</li><li>Framework and library authors who want an agnostic auth layer.</li><li>Anyone wanting to separate auth logic from transport and persistence.</li></ul><h3 id="why-this-is-important" tabindex="-1">Why This Is Important <a class="header-anchor" href="#why-this-is-important" aria-label="Permalink to &quot;Why This Is Important&quot;">​</a></h3><ul><li>Avoids monolithic auth solutions tied to specific transports or storage.</li><li>Encourages code reuse across projects with different auth needs.</li><li>Simplifies testing and maintenance by isolating strategies.</li></ul>',11)])])}const g=t(s,[["render",n]]);export{d as __pageData,g as default};
